mapper.select:
SELECT id, session_id, user_id, message_role, content, create_time, update_time
FROM chat_message
WHERE session_id = #{sessionId}
ORDER BY create_time ASC
LIMIT #{limit}

SELECT id, session_id, user_id, message_role, content, create_time, update_time
FROM chat_message
WHERE session_id = #{sessionId} AND user_id = #{userId}
ORDER BY create_time ASC
LIMIT #{limit}
