mapper.select:
SELECT id, session_id, user_id, session_title, create_time
FROM chat_session
WHERE session_id = #{sessionId}

SELECT id, session_id, user_id, session_title, create_time
FROM chat_session
WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{limit}

SELECT COUNT(*)
FROM chat_session
WHERE user_id = #{userId}

mapper.update:
UPDATE chat_session
SET session_title = #{title}
WHERE session_id = #{sessionId}
